import React from "react";
import useQuotes from "../UseQuotes";

const Quote = ({ quote }) => {
  const { deleteQuote } = useQuotes();

  const deleteClick = () => {
    deleteQuote(quote);
  };

  return (
    <div className="flex flex-row justify-between items-center">
      {quote}
      <button
        onClick={deleteClick}
        className="max-h-12 bg-red-300 hover:bg-red-400 text-gray-800 font-bold py-2 px-4 rounded-l"
        type="submit"
      >
        Delete
      </button>
    </div>
  );
};

export default Quote;
