import React from "react";
import { H1 } from "./Styles/Styled.H1";
import { H3 } from "./Styles/Styled.H3";
import { InputNumber } from "./Styles/Styled.InputNumber";
import { useContext } from "react";
import { AppContext } from "../app-context";

function Filter() {
  const [state, setState] = useContext(AppContext);

  const changeValue = (e) => {
    if (e.target.value >= 1) {
      setState({ ...state, filterAmount: e.target.value });
    }
  };
  return (
    <div style={{ flex: 1 }}>
      <H1>Kanye oneliners</H1>
      <H3>Word count filter:</H3>
      <InputNumber
        type="number"
        value={state.filterAmount}
        onChange={changeValue}
      ></InputNumber>
    </div>
  );
}

export default Filter;
